mapper.update:
update member
set mbrGenre =

	#{mbrGenre}

where mbrId = #{mbrId}

mapper.delete:
DELETE
FROM member
WHERE mbrId = #{mbrId}
AND mbrPw= #{mbrPw}

mapper.select:
select mbrId
from member
where mbrName=#{mbrName} and mbrEmail=#{mbrEmail}

select mbrPw
from member
where mbrId=#{mbrId} and mbrEmail=#{mbrEmail}

select count(*)
from member
where mbrId = #{mbrId}

select *
from member
where mbrId = #{mbrId} and mbrPw = #{mbrPw}
