mapper.update:
UPDATE review
SET reviewContent = #{reviewContent} , reviewScore= #{reviewScore}
WHERE reviewId=#{reviewId}

UPDATE review
SET reviewLiked = reviewLiked+1
WHERE reviewId=#{reviewId}

UPDATE review
SET reviewLiked = reviewLiked-1
WHERE reviewId=#{reviewId}

mapper.select:
SELECT reviewLiked
FROM review
WHERE reviewId=#{reviewId}

SELECT /*+ index_ss(review uniqueRvCheck) */ reviewId
FROM review
WHERE mbrName = #{mbrName} and gameNo = #{gameNo}

select /*+ index(review review_mbrName) */ count(*)
from review
where mbrName = #{mbrName}
