mapper.update:
UPDATE review
SET reviewContent = #{reviewContent} , reviewScore= #{reviewScore}
WHERE reviewId=#{reviewId}

mapper.select:
SELECT reviewLiked
FROM review
WHERE reviewId=#{reviewId}

mapper.update:
UPDATE reply
SET reviewLiked = reviewLiked+1
WHERE reviewId=#{reviewId}

UPDATE reply
SET reviewLiked = reviewLiked-1
WHERE reviewId=#{reviewId}

mapper.select:
SELECT reviewLiked
FROM review
WHERE reviewId=#{reviewId}
