mapper.update:
UPDATE board
SET boardCategory = #{boardCategory}, boardName = #{boardName}, boardContent = #{boardContent}
WHERE boardId = #{boardId}

		SELECT count(*)
        FROM liked
        WHERE boardId = #{boardId}
        AND mbrId = #{mbrId}

	UPDATE board
     SET boardLiked = boardLiked + 1
     WHERE boardId = #{boardId}

	UPDATE board
     SET boardLiked = boardLiked - 1
     WHERE boardId = #{boardId}
